import React, {useState,useEffect} from 'react';
import axios from 'axios';
import {withRouter, RouteComponentProps} from "react-router";
import {
    Avatar,
    Breadcrumbs as MuiBreadcrumbs,
    Button,
    Box,
    Card,
    CardContent,
    Divider,
    FormControl as MuiFormControl,
    Grid,
    Input,
    InputLabel,
    MenuItem,
    Link,
    TextField,
    Typography
  } from "@material-ui/core";

export default function UpdateExperience({ match }){
let params = match.params;
const[experience,setExperience] = useState({});
const [name,setName] = useState("");
const [position,setPosition] = useState("");
const [firstLine,setfirstLine] = useState("");
const [secondLine,setsecondLine] = useState("");
const [thirdLine,setthirdLine] = useState("");
let id = (params.id).substr(1);
useEffect(() => {
	axios.get('http://localhost:5000/experience/' +`${params.id}`)
	.then(response => setExperience(response.data))
	.catch(err => alert(err))

},[])

useEffect(() => {
    let name1 = experience.name;
	setName(name1);
	setPosition(experience.position);
    setfirstLine(experience.firstLine);
    setsecondLine(experience.secondLine);
    setthirdLine(experience.thirdLine);
},[])

const onSubmit = e =>{
      e.preventDefault();

      const experience2 = {
        name:name,
        position:position,
        firstLine:firstLine,
        secondLine:secondLine,
        thirdLine:thirdLine,
      }
      alert(JSON.stringify(experience));
  }
	return(
        <Card>
        <CardContent>
        <h1>ID:{params.id}</h1> 

        <TextField
          id="standard-helperText"
          label="Name"
          defaultValue={name}
          variant="outlined"
        />

        <h1>{experience.name}</h1>
        <Button onClick={onSubmit}> Click </Button>
        </CardContent>
        </Card>

	)

}
