import React, {useState,useEffect} from 'react';
import axios from 'axios';
import {withRouter, RouteComponentProps} from "react-router";
import {
    Avatar,
    Breadcrumbs as MuiBreadcrumbs,
    Button,
    Box,
    Card,
    CardContent,
    Divider,
    FormControl as MuiFormControl,
    Grid,
    Input,
    InputLabel,
    MenuItem,
    Link,
    TextField,
    Typography
  } from "@material-ui/core";

export default function UpdateExperience({ match }){
let params = match.params;
const[experience,setExperience] = useState({});

useEffect(() => {
	axios.get('http://localhost:5000/experience/' +`${params.id}`)
	.then(response => setExperience(response.data))
	.catch(err => alert(err))

},[])

const [name,setName] = useState(experience.name);
const [position,setPosition] = useState(experience.position);
const [firstLine,setfirstLine] = useState(experience.firstLine);
const [secondLine,setsecondLine] = useState(experience.secondLine);
const [thirdLine,setthirdLine] = useState(experience.thirdLine);

const onSubmit = e =>{
      e.preventDefault();

      const experience2 = {
        name:name,
        position:position,
        firstLine:firstLine,
        secondLine:secondLine,
        thirdLine:thirdLine,
      }
      alert(JSON.stringify(experience2));
  }
	return(
        <Card>
        <CardContent>
        <h1>ID:{experience._id}</h1> 

        <TextField
          id="standard-helperText"
          label="Name"
          value={name}
          defaultValue={name}
          variant="outlined"
        />

        <h1>{experience.name}</h1>
        <h1>{experience.position}</h1>
        <h1>{experience.firstLine}</h1>
        <h1>{experience.secondLine}</h1>
        <h1>{experience.thirdLine}</h1>
        <Button onClick={onSubmit}> Click {name} </Button>
        </CardContent>
        </Card>

	)

}
