import React, {useState,useEffect} from 'react';
import axios from 'axios';
import {withRouter, RouteComponentProps} from "react-router";
import {
    Avatar,
    Breadcrumbs as MuiBreadcrumbs,
    Button,
    Box,
    Card,
    CardContent,
    Divider,
    FormControl as MuiFormControl,
    Grid,
    Input,
    InputLabel,
    MenuItem,
    Link,
    TextField,
    Typography
  } from "@material-ui/core";

export default function UpdateExperience({ match }){
let params = match.params;
const[experience,setExperience] = useState({});
const[nameOne,setName] = useState("");


useEffect(() => {
	axios.get('http://localhost:5000/experience/' +`${params.id}`)
	.then(response => {
        setExperience(response.data)
    })
	.catch(err => alert(err))


},[])



const onSubmit = e =>{
      e.preventDefault();
      setName(experience.name.value)
      alert(nameOne);
      alert(JSON.stringify(experience));
  }
	return(
        <Card>
        <CardContent>
        <h1>ID:{experience._id}</h1> 

        <TextField
          id="standard-helperText"
          label="Name"
          value={nameOne}
          variant="outlined"
        />

        <h1>{experience.name}</h1>
        <Button onClick={onSubmit}> Submit </Button>
        </CardContent>
        </Card>

	)

}
